mapper.select:
SELECT * FROM APPROVAL A, USER_DB U 
WHERE A.USER_ID = U.USER_ID AND (A.USER_ID = #{user_id}


		AND A.A_TITLE LIKE CONCAT('%', #{searchKeyword}, '%')	  		


		AND A.A_CONTENT LIKE CONCAT('%', #{searchKeyword}, '%')	  		
	

 )
 ORDER BY A.A_NUM DESC LIMIT #{startPost}, #{viewPost}

SELECT COUNT(A_NUM) AS TOTALPOST FROM APPROVAL WHERE USER_ID = #{user_id}
AND A_TITLE LIKE CONCAT('%', #{searchKeyword}, '%')
AND A_CONTENT LIKE CONCAT('%', #{searchKeyword}, '%')

mapper.update:
UPDATE APPROVAL SET
        A_CONTENT = #{a_content},
		OK1 = #{ok1},	
		OK2 = #{ok2},
		OK3 = #{ok3}
	WHERE A_NUM = #{a_num}
